import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Slider } from "@/components/ui/slider";
import { Badge } from "@/components/ui/badge";
import { Settings, Shield, Bell, Palette, Clock, Save, User, AlertTriangle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

import { PageLayout } from "@/components/ui/main-navigation";

export default function SettingsPage() {
  const { toast } = useToast();

  const [settings, setSettings] = useState({
    // Risk Management
    riskTolerance: 2.0,
    maxPositionSize: 10000,
    stopLossEnabled: true,
    autoCloseEnabled: false,

    // Notifications
    notificationsEnabled: true,
    emailNotifications: true,
    pushNotifications: true,
    signalAlerts: true,
    newsAlerts: false,

    // Display & Performance
    theme: 'dark',
    refreshInterval: 60,
    chartTimeframe: '1h',
    compactMode: false,

    // Trading
    paperTradingMode: true,
    autoExecuteSignals: false,
    confirmBeforeTrade: true,
  });

  const saveSettingsMutation = useMutation({
    mutationFn: async (newSettings: typeof settings) => {
      return await apiRequest("POST", "/api/user/settings", {
        body: JSON.stringify(newSettings)
      });
    },
    onSuccess: () => {
      toast({
        title: "Settings Saved",
        description: "Your preferences have been updated successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Save Failed",
        description: "Failed to save settings. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSave = () => {
    saveSettingsMutation.mutate(settings);
  };

  const updateSetting = (key: string, value: any) => {
    setSettings(prev => ({ ...prev, [key]: value }));
  };

  return (
    <PageLayout>
      <div className="container mx-auto space-y-6 max-w-4xl">
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <Settings className="h-8 w-8 text-primary" />
          <h1 className="text-3xl font-bold">Settings</h1>
        </div>
        <Button onClick={handleSave} disabled={saveSettingsMutation.isPending}>
          <Save className="h-4 w-4 mr-2" />
          {saveSettingsMutation.isPending ? "Saving..." : "Save Changes"}
        </Button>
      </div>

      {/* Risk Management */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Shield className="h-5 w-5 text-green-500" />
            <span>Risk Management</span>
            {settings.paperTradingMode && (
              <Badge variant="outline" className="bg-blue-50 text-blue-600 border-blue-200">
                Paper Trading
              </Badge>
            )}
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-2">
              <Label htmlFor="risk-tolerance">Risk Tolerance (%)</Label>
              <div className="px-2">
                <Slider
                  value={[settings.riskTolerance]}
                  onValueChange={(value) => updateSetting('riskTolerance', value[0])}
                  max={10}
                  min={0.5}
                  step={0.5}
                  className="w-full"
                />
              </div>
              <div className="flex justify-between text-sm text-muted-foreground">
                <span>Conservative (0.5%)</span>
                <span className="font-medium">{settings.riskTolerance}%</span>
                <span>Aggressive (10%)</span>
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="max-position">Max Position Size ($)</Label>
              <Input
                id="max-position"
                type="number"
                value={settings.maxPositionSize}
                onChange={(e) => updateSetting('maxPositionSize', parseFloat(e.target.value))}
                min={100}
                max={100000}
                step={100}
              />
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="flex items-center justify-between">
              <div className="space-y-0.5">
                <Label>Stop Loss Protection</Label>
                <p className="text-sm text-muted-foreground">Automatically set stop losses</p>
              </div>
              <Switch
                checked={settings.stopLossEnabled}
                onCheckedChange={(checked) => updateSetting('stopLossEnabled', checked)}
              />
            </div>

            <div className="flex items-center justify-between">
              <div className="space-y-0.5">
                <Label>Auto Close Positions</Label>
                <p className="text-sm text-muted-foreground">Close positions at target profit</p>
              </div>
              <Switch
                checked={settings.autoCloseEnabled}
                onCheckedChange={(checked) => updateSetting('autoCloseEnabled', checked)}
              />
            </div>
          </div>

          {settings.riskTolerance > 5 && (
            <div className="flex items-center space-x-2 p-3 bg-yellow-50 dark:bg-yellow-950/20 rounded-lg border border-yellow-200">
              <AlertTriangle className="h-4 w-4 text-yellow-600" />
              <span className="text-sm text-yellow-700 dark:text-yellow-400">
                High risk tolerance selected. Consider reducing for safer trading.
              </span>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Notifications */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Bell className="h-5 w-5 text-blue-500" />
            <span>Notifications</span>
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="flex items-center justify-between">
              <div className="space-y-0.5">
                <Label>Enable Notifications</Label>
                <p className="text-sm text-muted-foreground">Master notification toggle</p>
              </div>
              <Switch
                checked={settings.notificationsEnabled}
                onCheckedChange={(checked) => updateSetting('notificationsEnabled', checked)}
              />
            </div>

            <div className="flex items-center justify-between">
              <div className="space-y-0.5">
                <Label>Email Notifications</Label>
                <p className="text-sm text-muted-foreground">Daily reports and alerts</p>
              </div>
              <Switch
                checked={settings.emailNotifications}
                onCheckedChange={(checked) => updateSetting('emailNotifications', checked)}
                disabled={!settings.notificationsEnabled}
              />
            </div>

            <div className="flex items-center justify-between">
              <div className="space-y-0.5">
                <Label>Signal Alerts</Label>
                <p className="text-sm text-muted-foreground">New trading signal notifications</p>
              </div>
              <Switch
                checked={settings.signalAlerts}
                onCheckedChange={(checked) => updateSetting('signalAlerts', checked)}
                disabled={!settings.notificationsEnabled}
              />
            </div>

            <div className="flex items-center justify-between">
              <div className="space-y-0.5">
                <Label>News Alerts</Label>
                <p className="text-sm text-muted-foreground">High-impact market news</p>
              </div>
              <Switch
                checked={settings.newsAlerts}
                onCheckedChange={(checked) => updateSetting('newsAlerts', checked)}
                disabled={!settings.notificationsEnabled}
              />
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Display & Performance */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Palette className="h-5 w-5 text-purple-500" />
            <span>Display & Performance</span>
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-2">
              <Label htmlFor="refresh-interval">Refresh Interval</Label>
              <Select
                value={settings.refreshInterval.toString()}
                onValueChange={(value) => updateSetting('refreshInterval', parseInt(value))}
              >
                <SelectTrigger>
                  <Clock className="h-4 w-4 mr-2" />
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="30">30 seconds</SelectItem>
                  <SelectItem value="60">1 minute</SelectItem>
                  <SelectItem value="300">5 minutes</SelectItem>
                  <SelectItem value="600">10 minutes</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label htmlFor="chart-timeframe">Default Chart Timeframe</Label>
              <Select
                value={settings.chartTimeframe}
                onValueChange={(value) => updateSetting('chartTimeframe', value)}
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="1m">1 minute</SelectItem>
                  <SelectItem value="5m">5 minutes</SelectItem>
                  <SelectItem value="15m">15 minutes</SelectItem>
                  <SelectItem value="1h">1 hour</SelectItem>
                  <SelectItem value="4h">4 hours</SelectItem>
                  <SelectItem value="1d">1 day</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          <div className="flex items-center justify-between">
            <div className="space-y-0.5">
              <Label>Compact Mode</Label>
              <p className="text-sm text-muted-foreground">Reduce spacing and padding</p>
            </div>
            <Switch
              checked={settings.compactMode}
              onCheckedChange={(checked) => updateSetting('compactMode', checked)}
            />
          </div>
        </CardContent>
      </Card>

      {/* Trading */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <User className="h-5 w-5 text-orange-500" />
            <span>Trading Preferences</span>
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="flex items-center justify-between">
              <div className="space-y-0.5">
                <Label>Paper Trading Mode</Label>
                <p className="text-sm text-muted-foreground">Practice with virtual money</p>
              </div>
              <Switch
                checked={settings.paperTradingMode}
                onCheckedChange={(checked) => updateSetting('paperTradingMode', checked)}
              />
            </div>

            <div className="flex items-center justify-between">
              <div className="space-y-0.5">
                <Label>Confirm Before Trade</Label>
                <p className="text-sm text-muted-foreground">Show confirmation dialog</p>
              </div>
              <Switch
                checked={settings.confirmBeforeTrade}
                onCheckedChange={(checked) => updateSetting('confirmBeforeTrade', checked)}
              />
            </div>
          </div>

          {!settings.paperTradingMode && (
            <div className="flex items-center space-x-2 p-3 bg-red-50 dark:bg-red-950/20 rounded-lg border border-red-200">
              <AlertTriangle className="h-4 w-4 text-red-600" />
              <span className="text-sm text-red-700 dark:text-red-400">
                Live trading mode enabled. Real money will be used for trades.
              </span>
            </div>
          )}
        </CardContent>
      </Card>
      </div>
    </PageLayout>
  );
}

client/src/pages/signals.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import { Zap, TrendingUp, TrendingDown, Target, Shield, Clock, BarChart3 } from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import { apiRequest } from "@/lib/queryClient";
import { useNotificationHelpers } from "@/components/ui/notification-system";
import TradeConfirmationModal from "@/components/ui/trade-confirmation-modal";

import { PageLayout } from "@/components/ui/main-navigation";

export default function Signals() {
  const [activeOnly, setActiveOnly] = useState(true);
  const [showConfirmationModal, setShowConfirmationModal] = useState(false);
  const [lastTrade, setLastTrade] = useState<any>(null);

  const { tradeExecuted } = useNotificationHelpers();
  const queryClient = useQueryClient();

  const { data: signals = [], isLoading } = useQuery({
    queryKey: ["/api/signals"],
    refetchInterval: 10000,
  });

  const executeSignalMutation = useMutation({
    mutationFn: async (signalId: string) => {
      const response = await apiRequest("POST", `/api/signals/execute/${signalId}`);
      return response.json();
    },
    onSuccess: (data) => {
      setLastTrade(data.tradeExecution);
      setShowConfirmationModal(true);

      tradeExecuted(
        data.tradeExecution.symbol,
        data.tradeExecution.action,
        data.tradeExecution.filledPrice
      );

      queryClient.invalidateQueries({ queryKey: ["/api/signals"] });
      queryClient.invalidateQueries({ queryKey: ["/api/positions"] });
      queryClient.invalidateQueries({ queryKey: ["/api/portfolio"] });
    },
  });

  const filteredSignals = activeOnly 
    ? signals.filter((signal: any) => signal.isActive !== false)
    : signals;

  if (isLoading) {
    return (
      <PageLayout>
        <div className="container mx-auto space-y-6">
          <div className="flex items-center justify-between">
            <h1 className="text-3xl font-bold">Trading Signals</h1>
          </div>
          <div className="space-y-4">
            {[...Array(3)].map((_, i) => (
              <Card key={i} className="animate-pulse">
                <CardContent className="p-6">
                  <div className="h-4 bg-muted rounded mb-2"></div>
                  <div className="h-4 bg-muted rounded"></div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      </PageLayout>
    );
  }

  return (
    <PageLayout>
      <div className="container mx-auto space-y-6">
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <Zap className="h-8 w-8 text-primary" />
          <h1 className="text-3xl font-bold">Trading Signals</h1>
        </div>
        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-2">
            <Switch
              id="active-only"
              checked={activeOnly}
              onCheckedChange={setActiveOnly}
            />
            <Label htmlFor="active-only">Active Only</Label>
          </div>
          <Badge variant="outline" className="flex items-center space-x-1">
            <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
            <span>Live AI Analysis</span>
          </Badge>
        </div>
      </div>

      {/* Signals Statistics */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-blue-600">{filteredSignals.length}</div>
            <div className="text-sm text-muted-foreground">Active Signals</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-green-600">
              {filteredSignals.filter((s: any) => s.action === 'BUY').length}
            </div>
            <div className="text-sm text-muted-foreground">Buy Signals</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-red-600">
              {filteredSignals.filter((s: any) => s.action === 'SELL').length}
            </div>
            <div className="text-sm text-muted-foreground">Sell Signals</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-orange-600">
              {Math.round(filteredSignals.reduce((avg: number, s: any) => avg + parseFloat(s.confidence || 0), 0) / filteredSignals.length || 0)}%
            </div>
            <div className="text-sm text-muted-foreground">Avg Confidence</div>
          </CardContent>
        </Card>
      </div>

      {/* Signals List */}
      <div className="space-y-4">
        {filteredSignals.length === 0 ? (
          <Card>
            <CardContent className="p-12 text-center">
              <Zap className="h-12 w-12 mx-auto mb-4 opacity-50" />
              <h3 className="text-lg font-medium mb-2">No signals available</h3>
              <p className="text-muted-foreground">
                {activeOnly ? "No active signals found. Try showing all signals." : "AI is analyzing market conditions..."}
              </p>
            </CardContent>
          </Card>
        ) : (
          filteredSignals.map((signal: any) => (
            <Card 
              key={signal.id} 
              className={`hover:shadow-lg transition-all duration-200 border-l-4 ${
                signal.action === "BUY" 
                  ? "border-l-green-500 bg-green-50/30 dark:bg-green-950/10" 
                  : "border-l-red-500 bg-red-50/30 dark:bg-red-950/10"
              }`}
            >
              <CardContent className="p-6">
                <div className="flex items-start justify-between mb-4">
                  <div className="flex items-center space-x-3">
                    <div className="flex items-center space-x-2">
                      {signal.action === "BUY" ? (
                        <TrendingUp className="h-6 w-6 text-green-600" />
                      ) : (
                        <TrendingDown className="h-6 w-6 text-red-600" />
                      )}
                      <h3 className="text-xl font-bold">{signal.symbol}</h3>
                    </div>
                    <Badge 
                      className={`text-sm font-medium px-3 py-1 ${
                        signal.action === "BUY" 
                          ? "bg-green-500 text-white hover:bg-green-600" 
                          : "bg-red-500 text-white hover:bg-red-600"
                      }`}
                    >
                      {signal.action}
                    </Badge>
                  </div>

                  <div className="text-right">
                    <div className="flex items-center space-x-2 mb-1">
                      <Shield className="h-4 w-4 text-blue-500" />
                      <span className="text-sm font-medium">Confidence</span>
                    </div>
                    <div className="text-2xl font-bold text-blue-600">
                      {signal.confidence}%
                    </div>
                  </div>
                </div>

                {/* Price Information */}
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
                  <div>
                    <div className="text-sm text-muted-foreground">Entry Price</div>
                    <div className="font-bold text-lg">
                      ${parseFloat(signal.price).toLocaleString('en-US', { minimumFractionDigits: 2 })}
                    </div>
                  </div>
                  {signal.targetPrice && (
                    <div>
                      <div className="text-sm text-muted-foreground">Target</div>
                      <div className="font-medium text-green-600">
                        ${parseFloat(signal.targetPrice).toLocaleString('en-US', { minimumFractionDigits: 2 })}
                      </div>
                    </div>
                  )}
                  {signal.stopLoss && (
                    <div>
                      <div className="text-sm text-muted-foreground">Stop Loss</div>
                      <div className="font-medium text-red-600">
                        ${parseFloat(signal.stopLoss).toLocaleString('en-US', { minimumFractionDigits: 2 })}
                      </div>
                    </div>
                  )}
                  <div>
                    <div className="text-sm text-muted-foreground">Potential Return</div>
                    <div className="font-medium text-blue-600">
                      {signal.targetPrice ? 
                        `${(((parseFloat(signal.targetPrice) - parseFloat(signal.price)) / parseFloat(signal.price)) * 100).toFixed(1)}%` :
                        "N/A"
                      }
                    </div>
                  </div>
                </div>

                {/* AI Analysis */}
                {signal.reasoning && (
                  <div className="mb-4 p-4 bg-muted/50 rounded-lg">
                    <div className="flex items-center space-x-2 mb-2">
                      <BarChart3 className="h-4 w-4 text-blue-500" />
                      <span className="text-sm font-medium">AI Analysis</span>
                    </div>
                    <p className="text-sm leading-relaxed">{signal.reasoning}</p>
                  </div>
                )}

                {/* Alternative Scenarios */}
                {signal.altScenarios && (
                  <div className="mb-4 p-4 bg-gradient-to-br from-blue-50/50 to-purple-50/50 dark:from-blue-950/20 dark:to-purple-950/20 rounded-lg border border-blue-200/50">
                    <div className="flex items-center space-x-2 mb-3">
                      <Target className="h-4 w-4 text-purple-500" />
                      <span className="text-sm font-medium">Risk Scenarios</span>
                    </div>
                    <div className="space-y-2">
                      {signal.altScenarios.ifBullish && (
                        <div className="flex items-start space-x-2">
                          <div className="w-2 h-2 rounded-full bg-green-500 mt-1.5 flex-shrink-0"></div>
                          <div>
                            <div className="text-xs font-medium text-green-600">Bullish Case</div>
                            <div className="text-xs text-muted-foreground">{signal.altScenarios.ifBullish}</div>
                          </div>
                        </div>
                      )}
                      {signal.altScenarios.ifBearish && (
                        <div className="flex items-start space-x-2">
                          <div className="w-2 h-2 rounded-full bg-red-500 mt-1.5 flex-shrink-0"></div>
                          <div>
                            <div className="text-xs font-medium text-red-600">Bearish Case</div>
                            <div className="text-xs text-muted-foreground">{signal.altScenarios.ifBearish}</div>
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                )}

                {/* Signal Metadata */}
                <div className="flex items-center justify-between text-sm text-muted-foreground mb-4">
                  <div className="flex items-center space-x-4">
                    <div className="flex items-center space-x-1">
                      <Clock className="h-3 w-3" />
                      <span>
                        {formatDistanceToNow(new Date(signal.createdAt), { addSuffix: true })}
                      </span>
                    </div>
                    <Badge variant="outline" className="text-xs">
                      {signal.source || 'AI Algorithm'}
                    </Badge>
                  </div>
                </div>

                {/* Action Buttons */}
                <div className="flex space-x-3">
                  <Button 
                    className={`flex-1 font-medium ${
                      signal.action === "BUY" 
                        ? "bg-green-600 hover:bg-green-700 text-white" 
                        : "bg-red-600 hover:bg-red-700 text-white"
                    }`}
                    onClick={() => executeSignalMutation.mutate(signal.id)}
                    disabled={executeSignalMutation.isPending}
                  >
                    {executeSignalMutation.isPending ? "Executing..." : `Execute ${signal.action}`}
                  </Button>
                  <Button variant="outline" className="px-6">
                    Dismiss
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>

      <TradeConfirmationModal 
        isOpen={showConfirmationModal}
        onClose={() => setShowConfirmationModal(false)}
        trade={lastTrade}
      />
      </div>
    </PageLayout>
  );
}

